import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Box, Typography, TextField, Button } from "@mui/material";

const Login: React.FC = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    if (email === "teste@teste.com" && password === "123456") {
      localStorage.setItem("authToken", "fakeToken123"); // Simula login
      navigate("/"); // Redireciona para a home
    } else {
      alert("Credenciais inválidas!");
    }
  };
  return (
    <Box className='App'>
      <Box sx={{ background: '#332F2F', width: '300px', height: '550px', borderRadius: '20px' }}>
        <Box sx={{ background: '#332F2F', width: '300px', height: '150px', borderTopLeftRadius: '20px', borderTopRightRadius: '20px' }}>
          {/* <Box
            component='img'
            src={Logo}
            alt='Produto'
            sx={{
              width: '160px',
              height: '160px',
              borderRadius: '5px',
              border: '1px solid #E3E3E3',
              opacity: 1,
              marginRight: '20px',
              '@media (max-width: 1350px)': {
                width: '257px'
              },
              '@media (max-width: 665px)': {
                marginRight: '0px'
              }
            }}
          /> */}
          <Typography sx={{ fontSize: '48px' }}>Atlas Endemico</Typography>
        </Box>
        <Box sx={{ background: '#ffffff', width: '300px', height: '400px', borderRadius: '20px', borderTopLeftRadius: '100px', borderTopRightRadius: '0px' }}>
          <Typography sx={{ color: "#000000", fontSize: "32px" }} pb={2}>Login</Typography>
          <TextField label="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <TextField label="Senha" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <Button variant="contained" onClick={handleLogin} sx={{ mt: 2 }}>Entrar</Button>
          <Typography sx={{ color: "#000000", fontSize: "20px", mt: 3 }}>
            Não tem conta? <Button onClick={() => navigate("/register")}>Cadastre-se</Button>
          </Typography>
        </Box>
      </Box>
    </Box>
  )
}

export default Login
